// 1. Get raw text & names from previous nodes
const rawText = $input.first().json.text;
// We access the names determined in the first Code Node to match them correctly
const bizAName = $('Code in JavaScript3').first().json.bizA_Name; 
const bizBName = $('Code in JavaScript3').first().json.bizB_Name;

// 2. Clean Markdown
const cleanJson = rawText.replace(/```json/g, '').replace(/```/g, '').trim();

// 3. Parse JSON
let aiData;
try {
  aiData = JSON.parse(cleanJson);
} catch (error) {
  aiData = { strengths: {}, weaknesses: {}, winner: "Error", summary: "Error" };
}

// 4. Helper: Find the list that matches the business name
// The AI might return "City Doctorâ„¢" while our variable is "City Doctor". We fuzzy match.
function getList(sourceObj, targetName) {
  if (!sourceObj || !targetName) return [];

  // 1. Try Exact Match
  if (sourceObj[targetName]) return sourceObj[targetName];

  // 2. Try Partial Match (Case insensitive)
  const keys = Object.keys(sourceObj);
  const match = keys.find(k => 
    k.toLowerCase().includes(targetName.toLowerCase()) || 
    targetName.toLowerCase().includes(k.toLowerCase())
  );

  return match ? sourceObj[match] : [];
}

// 5. Map to A and B
return {
  json: {
    // Global Fields
    winner: aiData.winner,
    summary: aiData.summary,
    recommendation: aiData.recommendation, // Kept global based on your JSON sample

    // Split Fields
    strengthsA: getList(aiData.strengths, bizAName),
    strengthsB: getList(aiData.strengths, bizBName),
    weaknessesA: getList(aiData.weaknesses, bizAName),
    weaknessesB: getList(aiData.weaknesses, bizBName)
  }
};

